import { useState, useCallback } from "react";
import Private from "./Private";
import PortalPopup from "./PortalPopup";
import styled from "styled-components";

const AccountChild = styled.div`
  position: absolute;
  top: 0px;
  left: 0px;
  border-radius: 9.98px;
  background-color: var(--color-white);
  width: 312px;
  height: 416px;
`;
const Image1Icon = styled.img`
  position: absolute;
  top: 0px;
  left: 0px;
  width: 299px;
  height: 394.62px;
  object-fit: cover;
`;
const GroupChild = styled.div`
  position: absolute;
  top: 0px;
  left: 0px;
  border-radius: var(--br-11xl);
  background-color: var(--color-white);
  border: 1px solid var(--color-lightskyblue);
  box-sizing: border-box;
  width: 285px;
  height: 52px;
`;
const ShowPrivateKey = styled.div`
  position: absolute;
  top: 13px;
  left: 60px;
  font-size: var(--paragraph-2-size);
  line-height: 28px;
  font-family: var(--font-noto-sans-kr);
  color: var(--color-cornflowerblue);
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 165px;
  height: 25px;
`;
const RectangleParent = styled.button`
  cursor: pointer;
  border: none;
  padding: 0;
  background-color: transparent;
  position: absolute;
  top: 338px;
  left: 7px;
  width: 285px;
  height: 52px;
`;
const VectorIcon = styled.img`
  position: absolute;
  height: 65.65%;
  width: 65.65%;
  top: 17.17%;
  right: 17.18%;
  bottom: 17.18%;
  left: 17.18%;
  max-width: 100%;
  overflow: hidden;
  max-height: 100%;
  object-fit: cover;
`;
const PhxBold = styled.button`
  cursor: pointer;
  border: none;
  padding: 0;
  background-color: transparent;
  position: absolute;
  top: 10px;
  left: 267px;
  width: 20px;
  height: 20px;
  overflow: hidden;
`;
const Image1Parent = styled.div`
  position: absolute;
  top: 11px;
  left: 13px;
  width: 299px;
  height: 394.62px;
`;
const AccountRoot = styled.div`
  width: 312px;
  height: 416px;
  max-width: 100%;
  max-height: 100%;
  overflow: auto;
`;
const Account = ({ onClose }) => {
  const [isPrivatePopupOpen, setPrivatePopupOpen] = useState(false);

  const openPrivatePopup = useCallback(() => {
    setPrivatePopupOpen(true);
  }, []);

  const closePrivatePopup = useCallback(() => {
    setPrivatePopupOpen(false);
  }, []);

  return (
    <>
      <AccountRoot>
        <AccountChild />
        <Image1Parent>
          <Image1Icon alt="" src="/.png" />
          <RectangleParent onClick={openPrivatePopup}>
            <GroupChild />
            <ShowPrivateKey>Show Private Key</ShowPrivateKey>
          </RectangleParent>
          <PhxBold onClick={onClose}>
            <VectorIcon alt="" src="/undefined13.png" />
          </PhxBold>
        </Image1Parent>
      </AccountRoot>
      {isPrivatePopupOpen && (
        <PortalPopup
          overlayColor="rgba(113, 113, 113, 0.3)"
          placement="Centered"
          onOutsideClick={closePrivatePopup}
        >
          <Private onClose={closePrivatePopup} />
        </PortalPopup>
      )}
    </>
  );
};

export default Account;
